import React from "react";


const Grid = function ({movies}) {
  console.log(movies);
  if (movies){
     return (
    <div className="columns is-multiline layout">
      {movies?.map((item, i) => (
        <div className="column is-3" key={i}>
          <div className="card">
            <div className="card-image">
              <figure className="image">
                <img src={"https://image.tmdb.org/t/p/original/" + item.poster_path} alt="Placeholder" />
              </figure>
            </div>
            <div className="card-content">
              <div className="media">
                <div className="media-left">
                  <div className="media-content">
                    <p className="title is-6">{item.title}</p>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
  } else {
    return (<h1>No hay datos</h1>)
  }

};

export default Grid;
