import axios from "axios";
import React, { useContext, useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { authContext } from "../contexts/AuthContext";

const Sidebar = function () {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [favorites, setFavorites] = useState([]);

  const navigate = useNavigate();

  const userContext = useContext(authContext);

  const onChangeEmail = (event) => {
    setEmail(event.target.value);
  };
  const onChangePassword = (event) => {
    setPassword(event.target.value);
  };

  const handleLogin = (e) => {
    e.preventDefault();
    axios
      .post("/api/users/login", {
        email: email,
        password: password,
      })
      .then((res) => res.data)
      .then((user) => userContext.toggleAuth(user))
      .catch((err) => console.log(err));
  };

  const handleLogout = (e) => {
    e.preventDefault();
    axios
      .post("/api/users/logout")
      .then((user) => userContext.toggleAuth(null))
      .then(() => navigate("/"))
      .catch((err) => console.log(err));
  };

  const handleDeleteFromFavorites = (favId) =>

  useEffect(() => {
    userContext.isAuthenticated &&
      axios
        .get(`/api/favorites/${userContext.user.id}`)
        .then((result) => setFavorites(result.data))
        .catch((err) => console.log(err));
  }, [userContext.isAuthenticated && userContext.user.id]);

  // const handleFavorites = (e) => {
  //   e.preventDefault();

  // };



  console.log("userContext-->", userContext);
  console.log("favorites-->", favorites);

  return !userContext.isAuthenticated ? (
    <aside className="menu column is-one-fifth">
      <div className="is-flex is-justify-content-space-between">
        <p className="menu-label has-text-weight-bold">LOGIN</p>
        {/* <Link to="/new-playlist">
        <button className="button is-success is-small">NEW</button>
      </Link> */}
      </div>
      <div className="container">
        <form onSubmit={handleLogin}>
          <div className="field">
            <label className="label">Email</label>
            <div className="control">
              <input
                onChange={onChangeEmail}
                className="input"
                type="email"
                placeholder="e.g. alexsmith@gmail.com"
              />
            </div>
          </div>
          <div className="field">
            <label className="label">Password</label>
            <div className="control">
              <input
                onChange={onChangePassword}
                className="input"
                type="password"
                placeholder="e.g Alex Smith"
              />
            </div>
          </div>
          <button className="button is-dark">Login</button>
          <p>OR</p>
        </form>
        <Link to="/register">
          <button className="button is-dark">Register</button>
        </Link>
      </div>
    </aside>
  ) : (
    <aside className="menu column is-one-fifth">
      <div className="is-flex is-justify-content-space-between">
        <p className="menu-label has-text-weight-bold">
          HELLO {userContext.user.username}!
        </p>
        {/* <Link to="/logout"> */}
        <button onClick={handleLogout} className="button is-success is-small">
          Logout
        </button>
        {/* </Link> */}
      </div>
      <br></br>
      <div className="is-flex is-justify-content-space-between">
        <p className="menu-label has-text-weight-bold">Your favorites list</p>
      </div>
      <div>
        {favorites.map((item, i) => (
          <div className="is-flex is-justify-content-space-between">
            <section key={item.id} class="section">
              {/* <h1 class="title">Section</h1> */}
              <h2 class="subtitle">{item.movieTitle}</h2>
              <button className="button is-success is-small">Delete</button>
            </section>
          </div>
        ))}
      </div>
    </aside>
  );
};

export default Sidebar;
