import axios from "axios";
import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";

const UserList = function () {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    axios
      .get(`/api/users`)
      .then((result) => result.data)
      .then((data) => setUsers(data));
  }, []);

