          <section
            style={{
              border: "2px solid black",
              borderRadius: "20px",
              margin: "5%",
            }}
            className="section is-small"
          >
            {/* <h1 class="title">Section</h1> */}
            <div className="is-flex is-justify-content-space-between">
              <h2 className="subtitle">{user.username}</h2>
              <p>- </p>
              <p>{user.fullname}</p>
              <p>- </p>

              <p>{user.email}</p>
            </div>
          </section>
        </Link>
      ))}
    </div>
  );
};

export default UserList;
