import { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

const Content = () => {
  const { id } = useParams();

  console.log("ID-->", id);

  const [data, setData] = useState({});

  useEffect(() => {
    const options = {
      method: "GET",
      url: "https://api.themoviedb.org/3/movie/" + id,
      headers: {
        accept: "application/json",
        Authorization:
          "Bearer eyJhbGciOiJIUzI1NiJ9.eyJhdWQiOiI0M2FlY2E1NmZkNGJiOTYzNDRlY2Q2MjFiY2Y3NGUyYiIsInN1YiI6IjY0ZWNiMDBiYzNjODkxMDExZGEwMGEzZCIsInNjb3BlcyI6WyJhcGlfcmVhZCJdLCJ2ZXJzaW9uIjoxfQ.PPqHm4cNxjZOFznfEKNaofQd6mIfaurqCtu9loSm8VE",
      },
    };

    axios
      .request(options)
      .then(response=> {
        setData(response.data);
      })
      .catch(function (error) {
        console.error(error);
      });
  }, []);

  // if (!data.id) return <h3>No hay datos</h3>;

  return (
    <section className="layout">
      <p>Contenido de la pelicula</p>
      <img
              src={"https://image.tmdb.org/t/p/original/" + item.poster_path}
              alt="Placeholder"
            />
    </section>
  );
};

export default Content;
