function getGenres(arrGenres) {
  if(!arrGenres) return
  let genres = arrGenres[0].name;
  if (arrGenres.length > 1) {
    for (let i = 1; i < arrGenres.length; i++) {
      genres += `, ${arrGenres[i].name}`;
    }
  }
  return genres;
}

module.exports = {getGenres}
