import React, { useEffect, useState } from "react";
import { Route, Routes } from "react-router-dom";
import Navbar from "./components/Navbar";
import axios from "axios";
import Grid from "./components/Grid";
import Content from "./components/Content";

const App = () => {
  const [movies, setMovies] = useState([]);
  const [inputValue, setInputValue] = useState("");

  useEffect(() => {
    const options = {
      method: "GET",
      url: "https://api.themoviedb.org/3/movie/popular?language=en-US&page=1",
      headers: {
        accept: "application/json",
        Authorization:
          "Bearer eyJhbGciOiJIUzI1NiJ9.eyJhdWQiOiI0M2FlY2E1NmZkNGJiOTYzNDRlY2Q2MjFiY2Y3NGUyYiIsInN1YiI6IjY0ZWNiMDBiYzNjODkxMDExZGEwMGEzZCIsInNjb3BlcyI6WyJhcGlfcmVhZCJdLCJ2ZXJzaW9uIjoxfQ.PPqHm4cNxjZOFznfEKNaofQd6mIfaurqCtu9loSm8VE",
      },
    };

    axios
      .request(options)
      .then(function (response) {
        setMovies(response.data.results);
      })
      .catch(function (error) {
        console.error(error);
      });
  }, []);

  const onChange = (event)=>{
    setInputValue(event.target.value)
  }

  console.log("movies-->", movies);
  console.log(inputValue);

  const handleSubmit = (event)=>{
    event.preventDefault()
    console.log("HandleSubmit-->", event.target.value);
  }

  return (
    <div>
      <Navbar />
      <Routes>
        <Route
          path="/"
          element={
            <div>
              <p>Â¡Bienvenidos a la TMDB!</p>
              <form onSubmit={handleSubmit}>
                <label className="label my-3">
                  <input onChange={onChange}

                    className="input my-3"
                    type="text"
                    placeholder="Search movies"
                  />
                </label>
              </form>
              <Grid movies={movies} />
            </div>
          }
        />

        <Route path="/movies/:id" element={<Content />} />
      </Routes>
    </div>
  );
};

export default App;
