import { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

const Content = () => {
  const { id } = useParams();

  console.log("ID-->", id);

  const [data, setData] = useState({});

  console.log("data-->", data);

  useEffect(() => {
    axios
      .request({
        method: "GET",
        url: "https://api.themoviedb.org/3/movie/" + id,
        headers: {
          accept: "application/json",
          Authorization:
            "Bearer eyJhbGciOiJIUzI1NiJ9.eyJhdWQiOiI0M2FlY2E1NmZkNGJiOTYzNDRlY2Q2MjFiY2Y3NGUyYiIsInN1YiI6IjY0ZWNiMDBiYzNjODkxMDExZGEwMGEzZCIsInNjb3BlcyI6WyJhcGlfcmVhZCJdLCJ2ZXJzaW9uIjoxfQ.PPqHm4cNxjZOFznfEKNaofQd6mIfaurqCtu9loSm8VE",
        },
      })
      .then((response) => setData(response.data))
      .catch(function (error) {
        console.error(error);
      });
  }, [id]);

  // if (!data.id) return <h3>No hay datos</h3>;
  console.log(data.genres);
  if (data.genres){

    let genres = data.genres[0].name
    if(data.genres.length>1){
      for(let i=1;i<data.genres.length;i++){
        genres += `, ${data.genres[i].name}`
      }
    }
    return genres
  }



  return (
    <section className="section">
      <h1 className="title">{data.title}</h1>
      <h2 class="subtitle">{data.release_date}</h2>
      <figure className="image is-256x256">
        <img
          src={"https://image.tmdb.org/t/p/original/" + data.poster_path}
          alt="Placeholder"
        />
      </figure>
      <div className="content">
        <p>OVERVIEW: {data.overview}</p>
        <p>GENRES: {genres}</p>
      </div>
    </section>
  );
};

export default Content;

