import { useContext, useEffect, useState } from "react";
import axios from "axios";
import { useNavigate, useParams } from "react-router-dom";
import {
  getGenres,
  dateSetter,
  scoreSetter,
  actorsSetter,
  directorsSetter,
} from "../utils/utils";
import { authContext } from "../contexts/AuthContext";

const Content = () => {
  const { type, id } = useParams();

  console.log("type---> ", type);

  const [data, setData] = useState({});
  const [actors, setActors] = useState([]);
  const [directors, setDirectors] = useState([]);

  const userContext = useContext(authContext);

  const navigate = useNavigate();

  //Request GET de la informacion de una pelicula especÃ­fica
  useEffect(() => {
    if (type === "movies") {
      axios
        .request({
          method: "GET",
          url: "https://api.themoviedb.org/3/movie/" + id,
          headers: {
            accept: "application/json",
            Authorization:
              "Bearer eyJhbGciOiJIUzI1NiJ9.eyJhdWQiOiI0M2FlY2E1NmZkNGJiOTYzNDRlY2Q2MjFiY2Y3NGUyYiIsInN1YiI6IjY0ZWNiMDBiYzNjODkxMDExZGEwMGEzZCIsInNjb3BlcyI6WyJhcGlfcmVhZCJdLCJ2ZXJzaW9uIjoxfQ.PPqHm4cNxjZOFznfEKNaofQd6mIfaurqCtu9loSm8VE",
          },
        })
        .then((response) => setData(response.data))
        .catch(function (error) {
          console.error(error);
        });
    } else if (type === "tvshows") {
      axios
        .request({
          method: "GET",
          url: "https://api.themoviedb.org/3/tv/" + id,
          headers: {
            accept: "application/json",
            Authorization:
              "Bearer eyJhbGciOiJIUzI1NiJ9.eyJhdWQiOiI0M2FlY2E1NmZkNGJiOTYzNDRlY2Q2MjFiY2Y3NGUyYiIsInN1YiI6IjY0ZWNiMDBiYzNjODkxMDExZGEwMGEzZCIsInNjb3BlcyI6WyJhcGlfcmVhZCJdLCJ2ZXJzaW9uIjoxfQ.PPqHm4cNxjZOFznfEKNaofQd6mIfaurqCtu9loSm8VE",
          },
        })
        .then(function (response) {
          setData(response.data);
        })
        .catch(function (error) {
          console.error(error);
        });
    }
  }, [id]);

  //Request GET para obtener los actores de una pelicula
  useEffect(() => {
    if (type === "movies") {
      axios
        .request({
          method: "GET",
          url: `https://api.themoviedb.org/3/movie/${id}/credits`,
          headers: {
            accept: "application/json",
            Authorization:
              "Bearer eyJhbGciOiJIUzI1NiJ9.eyJhdWQiOiI0M2FlY2E1NmZkNGJiOTYzNDRlY2Q2MjFiY2Y3NGUyYiIsInN1YiI6IjY0ZWNiMDBiYzNjODkxMDExZGEwMGEzZCIsInNjb3BlcyI6WyJhcGlfcmVhZCJdLCJ2ZXJzaW9uIjoxfQ.PPqHm4cNxjZOFznfEKNaofQd6mIfaurqCtu9loSm8VE",
          },
        })
        .then((response) => response.data)

        .then((data) => {
          setDirectors(data.crew.filter((person) => person.job === "Director"));
          setActors(data.cast);
        })
        .catch(function (error) {
          console.error(error);
        });
    }
  }, [id]);

  //Funcion para agregar a la lista de favoritos
  const addToFavorites = (movie) => {
    //Verificar si el usuario esta logueado
    if (!userContext.isAuthenticated) return alert("You need to be logged in");
    let isRepeated = false;
    //Pedido get para obtener los favoritos del usuario y evitar que se repitan
    axios
      .get(`/api/favorites/${userContext.user.id}`)
      .then((result) => result.data)
      .then((data) => {
        isRepeated = data.some((favMovies) => favMovies.movieId === movie.id);

        if (isRepeated)
          return alert("This movie is already in your favorites list");

        //POST a los favoritos del usuario
        axios
          .post("/api/favorites", {
            userId: userContext.user.id,
            movieTitle: movie.title,
            movieId: movie.id,
            movieImg: movie.poster_path,
          })
          .then((res) => res.data)
          .then(() => window.location.reload())
          .catch((err) => console.log(err));
      });
  };

  // if (!data.id) return <h3>No hay datos</h3>;
  console.log("DATA--->", data)
  console.log("actors---> ", actors);
  console.log("directors---> ", directors);

  return type === "movies" ? (
    <div className="container">
      <div
        className="notification "
        style={{ backgroundColor: "#D2E3E8", margin: "10px", color: "black" }}
      >
        <button
          onClick={() => {
            navigate(-1);
          }}
          class="button is-ghost"
        >
          Back
        </button>
        <div className="is-flex is-justify-content-space-between">
          <h1 className="title">{data.title}</h1>
          <button
            onClick={() => addToFavorites(data)}
            className="button is-light"
          >
            Add to favorites
          </button>
        </div>
        <h2 className="subtitle">{data.tagline}</h2>
        <h3 className="subtitle">
          {data.release_date && dateSetter(data.release_date)}
        </h3>
        <figure className="image">
          <img
            style={{ maxWidth: "30%", margin: "0 auto" }}
            src={"https://image.tmdb.org/t/p/original/" + data.poster_path}
            alt="Placeholder"
          />
        </figure>
        <br></br>
        <br></br>
        <br></br>
        <div className="content">
          <h3 className="subtitle">OVERVIEW</h3>
          <p> {data.overview}</p>
          <h3 className="subtitle">CAST</h3>
          <p> {actors[0] && actorsSetter(actors)}</p>
          <h3 className="subtitle">DIRECTOR</h3>
          <p> {directors[0] && directorsSetter(directors)}</p>
          <h3 className="subtitle">GENRES</h3>
          <p> {getGenres(data.genres)}</p>
          <h3 className="subtitle">SCORE</h3>
          <p> {scoreSetter(data.vote_average)}</p>
        </div>
      </div>
    </div>
  ) : (
    <div className="container">
      <div
        className="notification "
        style={{ backgroundColor: "#D2E3E8", margin: "10px", color: "black" }}
      >
        <button
          onClick={() => {
            navigate(-1);
          }}
          class="button is-ghost"
        >
          Back
        </button>
        <div className="is-flex is-justify-content-space-between">
          <h1 className="title">{data.name}</h1>
          <button
            onClick={() => addToFavorites(data)}
            className="button is-light"
          >
            Add to favorites
          </button>
        </div>
        <h2 className="subtitle">{data.tagline}</h2>
        <h3 className="subtitle">
          {data.release_date && dateSetter(data.first_air_date)}
        </h3>
        <figure className="image">
          <img
            style={{ maxWidth: "30%", margin: "0 auto" }}
            src={"https://image.tmdb.org/t/p/original/" + data.poster_path}
            alt="Placeholder"
          />
        </figure>
        <br></br>
        <br></br>
        <br></br>
        <div className="content">
          <h3 className="subtitle">OVERVIEW</h3>
          <p> {data.overview}</p>
          {/* <h3 className="subtitle">CAST</h3>
          <p> {actors[0] && actorsSetter(actors)}</p>
          <h3 className="subtitle">DIRECTOR</h3>
          <p> {directors[0] && directorsSetter(directors)}</p> */}
          <h3 className="subtitle">GENRES</h3>
          <p> {getGenres(data.genres)}</p>
          <h3 className="subtitle">SCORE</h3>
          <p> {scoreSetter(data.vote_average)}</p>
          <h3 className="subtitle">SEASONS</h3>
          <p> {data.seasons.length}</p>

        </div>
      </div>
    </div>
  );
};

export default Content;

// {data.genres ? data.genres[0].name : ""}
