import { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

const Content = () => {
  const { id } = useParams();

  console.log("ID-->", id);

  const [data, setData] = useState({});

  useEffect(() => {
    axios
      .request({
        method: "GET",
        url: "https://api.themoviedb.org/3/movie/" + id,
        headers: {
          accept: "application/json",
          Authorization:
            "Bearer eyJhbGciOiJIUzI1NiJ9.eyJhdWQiOiI0M2FlY2E1NmZkNGJiOTYzNDRlY2Q2MjFiY2Y3NGUyYiIsInN1YiI6IjY0ZWNiMDBiYzNjODkxMDExZGEwMGEzZCIsInNjb3BlcyI6WyJhcGlfcmVhZCJdLCJ2ZXJzaW9uIjoxfQ.PPqHm4cNxjZOFznfEKNaofQd6mIfaurqCtu9loSm8VE",
        },
      })
      .then((response) => setData(response.data))
      .catch(function (error) {
        console.error(error);
      });
  }, [id]);

  // if (!data.id) return <h3>No hay datos</h3>;

  return (
    // <section className="layout">
    //   <p>Contenido de la pelicula</p>
    //   <img
    //     src={"https://image.tmdb.org/t/p/original/" + data.poster_path}
    //     alt="Placeholder"
    //   />
    // </section>

    <section class="section">
      <h1 class="title">{data.title}</h1>
      <img
        src={"https://image.tmdb.org/t/p/original/" + data.poster_path}
        alt="Placeholder"
      />
      <h2 class="subtitle">
        <strong></strong>{data.overview}
      </h2>
    </section>
  );
};

export default Content;
