import React, { useEffect, useState } from "react";
import { Route, Routes } from "react-router-dom";
import Navbar from "./components/Navbar";
import axios from "axios";
import Grid from "./components/Grid";
import Content from "./components/Content";
import Search from "./components/Search";
import Login from "./commons/Login";
import Sidebar from "./components/Sidebar";

const App = () => {
  const [movies, setMovies] = useState([]);

  useEffect(() => {
    const options = {
      method: "GET",
      url: "https://api.themoviedb.org/3/movie/popular?language=en-US&page=1",
      headers: {
        accept: "application/json",
        Authorization:
          "Bearer eyJhbGciOiJIUzI1NiJ9.eyJhdWQiOiI0M2FlY2E1NmZkNGJiOTYzNDRlY2Q2MjFiY2Y3NGUyYiIsInN1YiI6IjY0ZWNiMDBiYzNjODkxMDExZGEwMGEzZCIsInNjb3BlcyI6WyJhcGlfcmVhZCJdLCJ2ZXJzaW9uIjoxfQ.PPqHm4cNxjZOFznfEKNaofQd6mIfaurqCtu9loSm8VE",
      },
    };

    axios
      .request(options)
      .then(function (response) {
        setMovies(response.data.results);
      })
      .catch(function (error) {
        console.error(error);
      });
  }, []);

  console.log(movies);

  return (
    <div>
      <Navbar />
      <div className="container is-fluid columns">
      <Sidebar />
      <Routes>
        <Route
          path="/"
          element={
            <div className="container is-fluid">
              <div className="notification has-background-grey-lighter">
                <h1 className="is-size-3">WELCOME TO THE MOVIE DATABASE</h1>
                <Grid movies={movies} />
              </div>
            </div>
          }
        />
        <Route path="/search" element={<Search />} />
        <Route path="/movies/:id" element={<Content />} />
        <Route path="/login" element={<Login/>}/>
      </Routes>
      </div>
    </div>
  );
};

export default App;
