import React from "react";
import Card from "../commons/Card";
import { Link } from "react-router-dom";

const Grid = function ({ movies }) {
  if (movies) {
    return (
      <div className="columns is-multiline layout">
        {movies.map((item, i) => (
          // <Link to={`/movies/${item.id}`}>
            <Card item={item} i={i} />
          // {/* </Link> */}
        ))}
      </div>
    );
  } else {
    return <h1>No hay datos</h1>;
  }
};

export default Grid;
