      .get("/api/playlists")
      .then((res) => res.data)
      .then((playlists) => setPlaylists(playlists.items))
      .catch(() =>
        console.error(
          "Necesitas loguearte con Spotify para obtener tus playlists."
        )
      );
    axios
      .get("/api/artists")
      .then((res) => res.data)
      .then((artists) => setArtists(artists.items))
      .catch(() =>
        console.error(
          "Necesitas loguearte con Spotify para obtener tus artistas."
        )
      );
    axios
      .get("/api/albums")
      .then((res) => res.data)
      .then((albums) => setAlbums(albums.items))
      .catch(() =>
        console.error(
          "Necesitas loguearte con Spotify para obtener tus artistas."
        )
      );
  }, []);

  return (
    <div>
      <Navbar />
      <Routes>
        <Route path="/" element={<p>Â¡Bienvenidos a la TMDB!</p>} />
      </Routes>
    </div>
  );
};

export default App;
