import { useContext, useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";
import { getGenres } from "../utils/utils";
import { dateSetter } from "../utils/utils";
import { scoreSetter } from "../utils/utils";
import { authContext } from "../contexts/AuthContext";

const Content = () => {
  const { id } = useParams();

  const [data, setData] = useState({});

  const userContext = useContext(authContext)

  useEffect(() => {
    axios
      .request({
        method: "GET",
        url: "https://api.themoviedb.org/3/movie/" + id,
        headers: {
          accept: "application/json",
          Authorization:
            "Bearer eyJhbGciOiJIUzI1NiJ9.eyJhdWQiOiI0M2FlY2E1NmZkNGJiOTYzNDRlY2Q2MjFiY2Y3NGUyYiIsInN1YiI6IjY0ZWNiMDBiYzNjODkxMDExZGEwMGEzZCIsInNjb3BlcyI6WyJhcGlfcmVhZCJdLCJ2ZXJzaW9uIjoxfQ.PPqHm4cNxjZOFznfEKNaofQd6mIfaurqCtu9loSm8VE",
        },
      })
      .then((response) => setData(response.data))
      .catch(function (error) {
        console.error(error);
      });
  }, [id]);

  const addToFavorites = (movie)=>{
    if (!userContext.isAuthenticated) return
    axios.post("/api/favorites",{
      userId:userContext.user.id,
      movieTitle: movie.title, 
      movieId: movie.id,
    })
    .then(res=>console.log(res))
    .catch(err=>console.log(err))
  }
  if (!data.id) return <h3>No hay datos</h3>;

  return (
    <div className="container">
      <div className="notification is-primary">
        <div className="is-flex is-justify-content-space-between">
          <h1 className="title">{data.title}</h1>
          <button onClick={()=>addToFavorites(data)} className="button is-link is-small">
            Add to favorites
          </button>
        </div>
        <h2 className="subtitle">{data.tagline}</h2>
        <h3 className="subtitle">
          {data.release_date && dateSetter(data.release_date)}
        </h3>
        <figure className="image is-128x128">
          <img
            src={"https://image.tmdb.org/t/p/original/" + data.poster_path}
            alt="Placeholder"
          />
        </figure>
        <br></br>
        <br></br>
        <br></br>
        <div className="content">
          <p>OVERVIEW: {data.overview}</p>
          <p>GENRES: {getGenres(data.genres)}</p>
          <p>SCORE: {scoreSetter(data.vote_average)}</p>
        </div>
      </div>
    </div>
  );
};

export default Content;

