import React from "react";
import { dateSetter } from "../utils/utils";

const Card = function ({item, i}) {
  return (

      <div className="card">
        <div className="card-image">
          <figure className="image">
            <img
              src={item.poster_path ? "https://image.tmdb.org/t/p/original/" + item.poster_path : "https://blog.sinapsis.agency/wp-content/uploads/2021/04/DEFINICIONES.-ERROR-404.png"}
              alt="Placeholder"
            />
          </figure>
        </div>
        <div className="card-content">
          <div className="media">
            <div className="media-left">
              <div className="media-content">
                <p className="title is-6">{item.title}</p>
                <p >{item.release_date && dateSetter(item.release_date)}</p>
              </div>
            </div>
          </div>
        </div>
      </div>

  );
};

export default Card;
