import axios from "axios";
import React, { useContext, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { authContext } from "../contexts/AuthContext";

const Sidebar = function () {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const navigate = useNavigate()

  const userContext = useContext(authContext)

  const onChangeEmail = (event) => {
    setEmail(event.target.value);
  };
  const onChangePassword = (event) => {
    setPassword(event.target.value);
  };

  const handleSubmit = (e)=>{
    e.preventDefault();
    axios
      .post("/api/users/login", {
        email: email,
        password: password,
      })
      .then((res) => (console.log(res)))
      // .then((data) => navigate(`/`))
      .catch(err=>console.log(err))
  }

  return (
    <aside className="menu column is-one-fifth">
      <div className="is-flex is-justify-content-space-between">
        <p className="menu-label has-text-weight-bold">LOGIN</p>
        {/* <Link to="/new-playlist">
        <button className="button is-success is-small">NEW</button>
      </Link> */}
      </div>
      <div className="container">
        <form onSubmit={handleSubmit}>
          <div className="field">
            <label className="label">Email</label>
            <div className="control">
              <input
              onChange={onChangeEmail}
                className="input"
                type="email"
                placeholder="e.g. alexsmith@gmail.com"
              />
            </div>
          </div>
          <div className="field">
            <label className="label">Password</label>
            <div className="control">
              <input
              onChange={onChangePassword}

                className="input"
                type="password"
                placeholder="e.g Alex Smith"
              />
            </div>
          </div>
          <button className="button is-dark">Login</button>
          <p>OR</p>
        </form>
          <Link to="/register">
            <button className="button is-dark">Register</button>
          </Link>
      </div>
    </aside>
  );
};

export default Sidebar;
